package repeated.substring.pattern;

/**
 * Created by xudeng on 2/13/17.
 */
public class Solution {

    public boolean repeatedSubstringPattern(String str) {
        int maxlenght = Math.floorDiv(str.length(), 2);
        for(int i = 1; i <= maxlenght; i ++)
        {
            String repeat = str.substring(0, i);
            if ((str.length() % repeat.length() == 0) && repeatedBy(str, repeat))
            {
                return true;
            }
        }
        return false;
    }

    private boolean repeatedBy(String str, String repeat)
    {
        int firstOccurence = -1;
        do {
            firstOccurence = str.indexOf(repeat);
            if (firstOccurence != 0)
            {
                return false;
            }
            str = str.substring(repeat.length());
        }while(firstOccurence == 0 && !str.equals(""));
        if (str.equals(""))
        {
            return true;
        }
        return false;
    }

    public static void main(String[] argus)
    {
        Solution main = new Solution();
        System.out.print(main.repeatedSubstringPattern(""));
    }
}
